mapper.update:
update store_device_passage
set store_device_id = #{storeDeviceId,jdbcType=INTEGER},
  passage_id = #{passageId,jdbcType=CHAR},
  status = #{status,jdbcType=INTEGER},
  data_reverse = #{dataReverse,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select
	
from store_device_passage t
where t.store_device_id = #{storeDeviceId}

select count(t.id) from store_device_passage t inner join store_device t2 on t.store_device_id = t2.id inner join device t3 on t2.device_id = t3.id inner join store t4 on t2.store_id = t4.id inner join passage t5 on t.passage_id = t5.id
and t4.id = #{storeId}
and t5.id = #{passageId}

select t.id, t4.name as storeName, t5.type, t5.name, t3.code, t3.ip, t.status, t.data_reverse, DATE_FORMAT(t3.last_upload_date, '%Y-%m-%d %H:%i') as lastUploadDate from store_device_passage t inner join store_device t2 on t.store_device_id = t2.id inner join device t3 on t2.device_id = t3.id inner join store t4 on t2.store_id = t4.id inner join passage t5 on t.passage_id = t5.id
and t4.id = #{storeId}
and t5.id = #{passageId}
order by t4.id, t5.id asc limit #{pageIndex}, #{pageSize}
