mapper.select:
select t.id, t2.name as brandName, t.name, t.business_start_time as bstartTime, t.business_end_time as bendTime, (select c.name from china c where c.id = (select c.parent_id from china c where c.id = t.city)) as province, (select c.name from china c where c.id = t.city) as city, DATE_FORMAT(t.create_date, '%Y-%m-%d %H:%i') as createDate from store t left outer join brand t2 on t.brand_id = t2.id
and ifnull(t.brand_id, '') <> ''
and t.brand_id = #{brandId}
and t.name like concat('%', #{name}, '%')
order by t.brand_id, t.city asc limit #{pageIndex}, #{pageSize}

select t.id, t.name, t.business_start_time as bstartTime, t.business_end_time as bendTime,
	DATE_FORMAT(t.create_date, '%Y-%m-%d  %H:%i') as createDate
from store t where t.parent_id = #{storeId}
